import React, { Component } from 'react'
import axios from 'axios'
import Grid from '../grid'
import { withStyles } from '@material-ui/core/styles'
import { fade } from '@material-ui/core/styles/colorManipulator'
import SearchIcon from '@material-ui/icons/Search'
import InputBase from '@material-ui/core/InputBase'
import { connect } from 'react-redux'
import { updateBooks } from '../../actions/simpleAction'

var API_KEY = 'AIzaSyCqFhKYDS8eyMpWykenvIWSG96pCAGqttU'
//var API_KEY = 'AIzaSyCe_fl6hNHl5dDVAmokI7X-mgb199BYmfE';

function BookObject(
  id,
  title,
  subtitle,
  author,
  averageRating,
  description,
  thumbnail,
  infoLink,
  ratingsCount,
  pageCount,
  categories
) {
  this.id = id
  this.title = title
  this.subtitle = subtitle
  this.author = author
  this.averageRating = averageRating
  this.description = description
  this.thumbnail = thumbnail
  this.infoLink = infoLink
  this.ratingsCount = ratingsCount
  this.pageCount = pageCount
  this.categories = categories
}

const styles = theme => ({
  search: {
    position: 'relative',
    borderRadius: theme.shape.borderRadius,
    backgroundColor: fade(theme.palette.common.blue, 0.15),
    '&:hover': {
      backgroundColor: fade(theme.palette.common.blue, 0.25)
    },
    marginLeft: 0,
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      marginLeft: theme.spacing.unit,
      width: 'auto'
    },
    margin: 10
  },
  searchIcon: {
    width: theme.spacing.unit * 10,
    height: '33px', 
    position: 'absolute',
    pointerEvents: 'none',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },
  inputRoot: {
    color: 'inherit',
    width: '100%'
  },
  inputInput: {
    paddingTop: theme.spacing.unit,
    paddingRight: theme.spacing.unit,
    paddingBottom: theme.spacing.unit,
    paddingLeft: theme.spacing.unit * 10,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      width: 120,
      '&:focus': {
        width: 200
      }
    }
  }
})

class BookData extends Component {
  constructor(props) {
    super(props)
    this.state = {
      query: ' '
    }
  }

  handleInputChange = event => {
    this.setState({ query: event.target.value }, () => {
      if (this.state.query && this.state.query.length >= 1) {
        axios
          .get(
            `https://www.googleapis.com/books/v1/volumes?q=${
              this.state.query
            }&key=${API_KEY}`
          )
          .then(res => {
            var columns = []
            var listofBooks = {}

            if (res.data.items !== undefined) {
              res.data.items.forEach(function(element) {
                var newBookID = element.id
                var item = element.volumeInfo
                var authors = []

                if (item.authors === undefined) {
                  authors = null
                } else {
                  authors = item.authors
                }

                var newBook = new BookObject(
                  newBookID,
                  item.title,
                  item.subtitle,
                  authors,
                  item.averageRating,
                  item.description,
                  item.imageLinks,
                  item.infoLink,
                  item.ratingsCount,
                  item.pageCount,
                  item.categories
                )

                listofBooks[newBookID] = newBook
                columns.push(newBookID)
              })
            }
            this.props.updateBooks(listofBooks)
          })
      }
    })
  }

  render() {
    const { classes } = this.props

    return (
      <div className={classes.search}>
        <div className={classes.searchIcon}>
          <SearchIcon />
        </div>
        <InputBase
          placeholder="Search for booksâ€¦"
          classes={{
            root: classes.inputRoot,
            input: classes.inputInput
          }}
          onChange={this.handleInputChange}
        />
        <Grid />
      </div>
    )
  }
}

function mapDispatchToProps(dispatch) {
  return {
    updateBooks: books => dispatch(updateBooks(books))
  }
}

export default connect(
  null,
  mapDispatchToProps
)(withStyles(styles)(BookData))
